-- 用户数据同步脚本
-- 将image_translator数据库的用户数据同步到当前项目数据库
-- 策略：image_translator数据覆盖当前项目数据

-- 开始事务
START TRANSACTION;

-- 备份当前用户表（可选，用于回滚）
CREATE TABLE IF NOT EXISTS users_backup_20251023 AS SELECT * FROM users;

-- 清空当前用户表
DELETE FROM users;

-- 重置自增ID
ALTER TABLE users AUTO_INCREMENT = 1;

-- 插入image_translator数据库的用户数据（完整真实数据）

-- 更新自增ID起始值
ALTER TABLE users AUTO_INCREMENT = 54;

-- 提交事务
COMMIT;

-- 验证同步结果
SELECT COUNT(*) as total_users FROM users;
SELECT id, username FROM users ORDER BY id;
